const createOtp = (options) => {
  if (options) {
    const { alphaNum, limit } = options;
    if (!alphaNum) {
      let range = 1;
      for (let i = 0; i < limit; i++) range = range * 10;
      return Math.floor(Math.random() * range);
    } else if (alphaNum) {
      if (limit) {
        return Math.random().toString(36).slice(2, limit + 2);
      } else {
        return Math.random().toString(36).slice(2, 7);
      }
    }
  } else {
    return Math.floor(Math.random() * (100000 - 10000) - 100);
  }
};

export default createOtp;
